$(function () {
  var template = $('#article__template').html();
  var html = tmpl(template, {data: data});
  $('#article__wrapper').html(html);
  var overlay = $('.article__box__inner');
  overlay.on('mouseenter', function () {
    $(this).find('.article__box__inner__overlay').fadeIn(400);
  }).on('mouseleave', function () {
    $(this).find('.article__box__inner__overlay').fadeOut(400);
  })
})
